@dataclass
class Record:
	""" Easier to emulate a VCF Record rather than instantiate vcf._Record"""
	ALT: List[str]
	REF: str
	QUAL: Optional[float]
	INFO: Dict[str, Any]
	CHROM: str
	POS: int
	var_type: str


class TestVCFParser(unittest.TestCase):
	def test_get_vcf_filename(self):
		self.assertEqual(vcf_filename, get_vcf_filename(vcf_filename))

